export const getViewProd = createAsyncThunk(
    "viewProd/getViewProd",
    async (prodId, { rejectedWithValue }) => {
        try {
            const res = await api.get("/products/" + prodId);
            return res.data;
        } catch (error) {
            return rejectedWithValue(error.message);
        }
    }
)

const viewProdSlice = createSlice({
    name: "viewProd",
    initialState: {
        isVisible: false,
        isLoadingProd: false,
        current_scroll_y: null,
        prodData: []
    },
    reducers: {
        showViewProd: (state) => {
            state.current_scroll_y = window.scrollY;
            state.isVisible = true;
        },
        hideViewProd: (state) => {
            state.isVisible = false;
            window.scrollTo({ top: state.current_scroll_y ,behavior:"smooth"})
        }
    },
    extraReducers: (builder) => {
        builder
            .addCase(getViewProd.pending, (state) => {
                state.isLoadingProd = true;
                state.prodData = [];
            })
            .addCase(getViewProd.fulfilled, (state, action) => {
                state.isLoadingProd = false;
                state.prodData = action.payload;
            })
            .addCase(getViewProd.rejected, (state, action) => {
                state.isLoadingProd = false;
            })

        // -------------------

    }
})
export const { showViewProd, hideViewProd } = viewProdSlice.actions;
export default viewProdSlice.reducer;

